import React from 'react';
import useStyles from './styles';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import { useDispatch } from 'react-redux';
import { deleteFlight } from '../../../actions/flights';
import Form, { handleOpen } from '../../Form/Form';




const Flight = ({ flight, setCurrentId }) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const setAndOpen = () => {
        setCurrentId(flight._id);
        handleOpen();
    }
    
    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={'https://www.solidbackgrounds.com/images/1920x1080/1920x1080-bright-green-solid-color-background.jpg'}></CardMedia>
            <div className={classes.overlay}>
                <Typography variant="h4">#{flight.flightNumber}</Typography>
            </div>
            <div className={classes.overlay2}>
                <Button style={{color: 'white'}} size="small" onClick={() => Form}>
                    <MoreHorizIcon fontSize="large" />
                </Button> 
            </div>
            <CardContent>
                <Typography className={classes.flightNumber} variant="h5" gutterBottom>{flight.departureAirport} -{'>'} {flight.arrivalAirport}</Typography>
                <Typography className={classes.flightNumber} variant="h6" gutterBottom>{flight.departureDate} -{'>'} {flight.arrivalDate}</Typography>
                <Typography className={classes.flightNumber} variant="h6" gutterBottom>{flight.departureTime} -{'>'} {flight.arrivalTime}</Typography>
                <Typography className={classes.flightNumber} variant="h6" gutterBottom>Passengers: {flight.passengerCount}/{flight.passengerLimit}</Typography>

            </CardContent>
            <CardActions className={classes.cardActions}>
                <Button size="small" color="primary" onClick={() => dispatch(deleteFlight(flight._id))}> 
                    <DeleteIcon fontSize="small" />
                    Delete
                </Button>
            </CardActions>

        </Card>
    );
}

export default Flight;
