import React, { useState, useEffect } from 'react';
import { Card, CardActions, CardContent, CardMedia, Container, CssBaseline, AppBar, Typography, Grow, Grid, Toolbar } from '@material-ui/core';
import { useDispatch } from 'react-redux';

import { getFlights } from './actions/flights';
import Flights from './components/Flights/Flights';
import Form from './components/Form/Form';
import oceanicImage from './images/oceanicImage.png';
import useStyles from './styles';
const App = () => {
    const [currentId, setCurrentId] = useState(null);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getFlights());
    }, [currentId, dispatch]);

    return (
        <>
        <CssBaseline/>
        
        <Container maxwidth="lg">
            <AppBar className={classes.appBar} position="static" color="inherit">
                <Typography className= {classes.heading} variant="h2" align="center">Flight Service</Typography> 
                <img className={classes.image} src={oceanicImage} alt="oceanicImage" height="60" />
                <Form currentId={currentId} setCurrentId={setCurrentId}/>
            </AppBar>
            <main></main>
                <Container>
                    <Card container justifyContent="center" alignItems="stretch" spacing={10}>
                        <Card item xs={12} sm={7}>
                            <Flights setCurrentId={setCurrentId} />
                        </Card>
                    </Card>   
                </Container>
        </Container>
        </>
    );
}

export default App;
