import React from 'react';
import useStyles from './styles';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';


const Flight = ({ flight, setCurrentId }) => {
  //const dispatch = useDispatch();
  const classes = useStyles();

    return (
        <Card className={classes.card}>
            <CardMedia  className={classes.media} image={flight.selectedFile} />
            <div className={classes.overlay}>
                <Typography variant="h6">#{flight.flightNumber}</Typography>
            </div>
            <div className={classes.overlay2}>
                <Button style={{color: 'white'}} size="small" onClick={() => setCurrentId(flight._id)}>
                    <MoreHorizIcon fontSize="default" />
                </Button> 
            </div>
            <CardContent>
                <Typography className={classes.flightNumber} variant="h5" gutterBottom>{flight.departureAirport} -> {flight.arrivalAirport}</Typography>
                <Typography className={classes.flightNumber} variant="h6" gutterBottom>{flight.departureTime} -> {flight.arrivalTime}</Typography>
                <Typography className={classes.flightNumber} variant="h6" gutterBottom>Passengers: {flight.passengerCount} / Limit:{flight.passengerLimit}</Typography>

            </CardContent>
            <CardActions className={classes.cardActions}>
                <Button size="small" color="primary" onClick={() => {}}> 
                    <DeleteIcon fontSize="small" />
                    Delete
                </Button>
            </CardActions>

        </Card>
    );
}

export default Flight;
